const { doProtoWithJson, getPcComponentPath, writeFile } = require('../util')
const generatePcComponent = require('./generate_pc_component')

function buildProto({ protoNested, protoName }) {
  const componentContent = generatePcComponent({ protoNested, protoName })

  writeFile(getPcComponentPath(protoName), componentContent)
}

doProtoWithJson('component', buildProto)
