import { Request } from '../util/request'
import {
  CreateAccountRequest, CreateAccountResponse,
  UpdateAccountRequest, UpdateAccountResponse,
  DeleteAccountRequest, DeleteAccountResponse,
  GetAccountRequest, GetAccountResponse,
  CreateProfileRequest, CreateProfileResponse,
  UpdateProfileRequest, UpdateProfileResponse,
  GetProfileRequest, GetProfileResponse,
  ListProfileRequest, ListProfileResponse,
} from './types'
export const CreateAccount = (r?: CreateAccountRequest, codes?: number[]) =>
  Request<CreateAccountResponse>('/ceres/account/AccountService/CreateAccount')
    .code([...(codes || [])])
    .data(r || {})
    .run()
CreateAccount.url = '/ceres/account/AccountService/CreateAccount'
