import { Request } from '../util/request'
import {
  PatchRequest, PatchResponse,
  QueryRequest, QueryResponse,
  SysQueryRequest, SysQueryResponse,
  SysResetRequest, SysResetResponse,
} from './types'

export const Query = (r?: QueryRequest, codes?: number[]) =>
  Request<QueryResponse>('/ceres/analytics/AnalyticsService/Query')
    .code([...(codes || [])])
    .data(r || {})
    .run()
Query.url = '/ceres/analytics/AnalyticsService/Query'
