import { Request } from '../util/request'
import {
  CreateTaskRequest, CreateTaskResponse,
  DeleteTaskRequest, DeleteTaskResponse,
  GetTaskRequest, GetTaskResponse,
  ListTaskRequest, ListTaskResponse,
} from './types'



export const ListTask = (r?: ListTaskRequest, codes?: number[]) =>
  Request<ListTaskResponse>('/ceres/asynctask/AsyncTaskService/ListTask')
    .code([...(codes || [])])
    .data(r || {})
    .run()
ListTask.url = '/ceres/asynctask/AsyncTaskService/ListTask'