import { Request } from '../util/request'
import {
  UploadFileRequest, UploadFileResponse,
  GetPresignedURLRequest, GetPresignedURLResponse,
  SendSmsRequest, SendSmsResponse,
  GetQiniuUploadTokenRequest, GetQiniuUploadTokenResponse,
} from './types'

export const GetPresignedURL = (r?: GetPresignedURLRequest, codes?: number[]) =>
  Request<GetPresignedURLResponse>('/ceres/cloudapi/CloudApiService/GetPresignedURL')
    .code([...(codes || [])])
    .data(r || {})
    .run()
GetPresignedURL.url = '/ceres/cloudapi/CloudApiService/GetPresignedURL'

