export const Select_CloudType: FC<SelectEnumProps<number>> = ({ ...rest }) => {
  return <SelectEnum {...rest} data={list_CloudType} />
}

export const Map_CloudType: FC<MapProps> = ({ value }) => {
  return <>{map_CloudType[value]}</>
}

