import { Request } from '../util/request'
import {
  QueryRequest, QueryResponse,
  GetProductionProduceTaskDataRequest, GetProductionProduceTaskDataResponse,
  GetProductionPackTaskDataRequest, GetProductionPackTaskDataResponse,
} from './types'
export const Query = (r?: QueryRequest, codes?: number[]) =>
  Request<QueryResponse>('/ceres/databi/DataBIService/Query')
    .code([...(codes || [])])
    .data(r || {})
    .run()
Query.url = '/ceres/databi/DataBIService/Query'
