import { Request } from '../util/request'
import {
  GetTaskInfoRequest, GetTaskInfoResponse,
  GetDriverInfoRequest, GetDriverInfoResponse,
  ListTaskRequest, ListTaskResponse,
} from './types'
export const GetTaskInfo = (r?: GetTaskInfoRequest, codes?: number[]) =>
  Request<GetTaskInfoResponse>('/ceres/driverapp/DriverAppService/GetTaskInfo')
    .code([...(codes || [])])
    .data(r || {})
    .run()
GetTaskInfo.url = '/ceres/driverapp/DriverAppService/GetTaskInfo'
export const GetDriverInfo = (r?: GetDriverInfoRequest, codes?: number[]) =>
  Request<GetDriverInfoResponse>('/ceres/driverapp/DriverAppService/GetDriverInfo')
    .code([...(codes || [])])
    .data(r || {})
    .run()
GetDriverInfo.url = '/ceres/driverapp/DriverAppService/GetDriverInfo'
export const ListTask = (r?: ListTaskRequest, codes?: number[]) =>
  Request<ListTaskResponse>('/ceres/driverapp/DriverAppService/ListTask')
    .code([...(codes || [])])
    .data(r || {})
    .run()
ListTask.url = '/ceres/driverapp/DriverAppService/ListTask'