export const ListSettleSheet = (r?: ListSettleSheetRequest, codes?: number[]) =>
  Request<ListSettleSheetResponse>('/ceres/finance/FinanceService/ListSettleSheet')
    .code([...(codes || [])])
    .data(r || {})
    .run()
ListSettleSheet.url = '/ceres/finance/FinanceService/ListSettleSheet'
