import { Request } from '../util/request'
import {
  InitializeSystemRequest, InitializeSystemResponse,
  InitializeTestingGroupRequest, InitializeTestingGroupResponse,
  CleanGroupTicketRequest, CleanGroupTicketResponse,
  CleanGroupDataRequest, CleanGroupDataResponse,
} from './types'
export const InitializeSystem = (r?: InitializeSystemRequest, codes?: number[]) =>
  Request<InitializeSystemResponse>('/ceres/initialization/InitializationService/InitializeSystem')
    .code([...(codes || [])])
    .data(r || {})
    .run()
InitializeSystem.url = '/ceres/initialization/InitializationService/InitializeSystem'
