import { Request } from '../util/request'
import {
  GetUserInfoRequest, GetUserInfoResponse,
  UpdateUserInfoRequest, UpdateUserInfoResponse,
  AuthorizeRequest, AuthorizeResponse,
  TokenRequest, TokenResponse,
} from './types'
export const GetUserInfo = (r?: GetUserInfoRequest, codes?: number[]) =>
  Request<GetUserInfoResponse>('/ceres/oauth/OAuthService/GetUserInfo')
    .code([...(codes || [])])
    .data(r || {})
    .run()
GetUserInfo.url = '/ceres/oauth/OAuthService/GetUserInfo'
