import { Request } from '../util/request'
import {
  CreateOperationRequest, CreateOperationResponse,
  ListOperationRequest, ListOperationResponse,
} from './types'
export const CreateOperation = (r?: CreateOperationRequest, codes?: number[]) =>
  Request<CreateOperationResponse>('/ceres/operation/OperationService/CreateOperation')
    .code([...(codes || [])])
    .data(r || {})
    .run()
CreateOperation.url = '/ceres/operation/OperationService/CreateOperation'
export const ListOperation = (r?: ListOperationRequest, codes?: number[]) =>
  Request<ListOperationResponse>('/ceres/operation/OperationService/ListOperation')
    .code([...(codes || [])])
    .data(r || {})
    .run()
ListOperation.url = '/ceres/operation/OperationService/ListOperation'