import { Request } from '../util/request'
import {
  PinyinRequest, PinyinResponse,
} from './types'
export const Pinyin = (r?: PinyinRequest, codes?: number[]) =>
  Request<PinyinResponse>('/ceres/toolbox/ToolboxService/Pinyin')
    .code([...(codes || [])])
    .data(r || {})
    .run()
Pinyin.url = '/ceres/toolbox/ToolboxService/Pinyin'