import { Request } from '../util/request'
import {
  SendCodeRequest, SendCodeResponse,
  CheckCodeRequest, CheckCodeResponse,
} from './types'
export const SendCode = (r?: SendCodeRequest, codes?: number[]) =>
  Request<SendCodeResponse>('/ceres/verification/VerificationService/SendCode')
    .code([...(codes || [])])
    .data(r || {})
    .run()
SendCode.url = '/ceres/verification/VerificationService/SendCode'
